contracts/GroupPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title GroupPool
 * @dev Collaborative savings pool contract for group goals
 * @author LoopFi Team
 */
contract GroupPool is ReentrancyGuard, Ownable {
    // Events
    event PoolCreated(
        uint256 indexed poolId,
        address indexed creator,
        string name,
        uint256 targetAmount,
        uint256 maxMembers,
        uint256 lockDuration
    );
    event MemberJoined(uint256 indexed poolId, address indexed member, uint256 contribution);
    event ContributionMade(uint256 indexed poolId, address indexed member, uint256 amount);
    event PoolCompleted(uint256 indexed poolId, uint256 totalAmount, uint256 yieldEarned);
    event FundsDistributed(uint256 indexed poolId, address indexed member, uint256 amount);
    event ReferralBonusPaid(uint256 indexed poolId, address indexed referrer, uint256 bonus);

    // Structs
    struct Pool {
        uint256 poolId;
        address creator;
        string name;
        string description;
        uint256 targetAmount;
        uint256 currentAmount;
        uint256 maxMembers;
        uint256 currentMembers;
        uint256 lockDuration;
        uint256 startTime;
        uint256 endTime;
        bool isCompleted;
        bool isActive;
        uint256 yieldEarned;
        uint256 apy; // Annual Percentage Yield in basis points
        address[] members;
        mapping(address => uint256) memberContributions;
        mapping(address => bool) isMember;
        mapping(address => bool) hasWithdrawn;
    }

    struct MemberInfo {
        address member;
        uint256 contribution;
        uint256 share;
        bool hasWithdrawn;
    }

    // State variables
    mapping(uint256 => Pool) public pools;
    mapping(address => uint256[]) public userPools;
    mapping(address => uint256[]) public userCreatedPools;
    
    IERC20 public celoToken;
    IERC20 public cusdToken;
    
    uint256 public nextPoolId = 1;
    uint256 public constant MIN_TARGET_AMOUNT = 0.1 ether; // Minimum 0.1 CELO
    uint256 public constant MAX_TARGET_AMOUNT = 10000 ether; // Maximum 10,000 CELO
    uint256 public constant MIN_LOCK_DURATION = 7 days; // Minimum 7 days
    uint256 public constant MAX_LOCK_DURATION = 365 days; // Maximum 1 year
    uint256 public constant MIN_MEMBERS = 2; // Minimum 2 members
    uint256 public constant MAX_MEMBERS = 50; // Maximum 50 members
    
    uint256 public DEFAULT_APY = 1000; // 10% APY in basis points (higher for groups)
    uint256 public constant YIELD_FEE_PERCENTAGE = 200; // 2% fee on yield
    uint256 public constant REFERRAL_BONUS_PERCENTAGE = 100; // 1% referral bonus
    uint256 public constant CREATOR_BONUS_PERCENTAGE = 200; // 2% creator bonus
    
    address public treasury;
    uint256 public totalPoolsCreated;
    uint256 public totalVolumeLocked;

    // Modifiers
    modifier poolExists(uint256 poolId) {
        require(poolId > 0 && poolId < nextPoolId, "GroupPool: Pool does not exist");
        _;
    }

    modifier poolActive(uint256 poolId) {
        require(pools[poolId].isActive, "GroupPool: Pool not active");
        _;
    }

    modifier poolNotCompleted(uint256 poolId) {
        require(!pools[poolId].isCompleted, "GroupPool: Pool already completed");
        _;
    }

    modifier lockPeriodEnded(uint256 poolId) {
        require(block.timestamp >= pools[poolId].endTime, "GroupPool: Lock period not ended");
        _;
    }

    constructor(address _celoToken, address _cusdToken, address _treasury) Ownable(msg.sender) {
        celoToken = IERC20(_celoToken);
        cusdToken = IERC20(_cusdToken);
        treasury = _treasury;
    }

    /**
     * @dev Create a new group pool
     * @param name Pool name
     * @param description Pool description
     * @param targetAmount Target amount to save (in wei)
     * @param maxMembers Maximum number of members
     * @param lockDuration Duration to lock funds (in seconds)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function createPool(
        string memory name,
        string memory description,
        uint256 targetAmount,
        uint256 maxMembers,
        uint256 lockDuration,
        bool useCUSD
    ) external {
        require(bytes(name).length > 0, "GroupPool: Name required");
        require(targetAmount >= MIN_TARGET_AMOUNT, "GroupPool: Target amount too low");
        require(targetAmount <= MAX_TARGET_AMOUNT, "GroupPool: Target amount too high");
        require(maxMembers >= MIN_MEMBERS, "GroupPool: Too few members");
        require(maxMembers <= MAX_MEMBERS, "GroupPool: Too many members");
        require(lockDuration >= MIN_LOCK_DURATION, "GroupPool: Lock duration too short");
        require(lockDuration <= MAX_LOCK_DURATION, "GroupPool: Lock duration too long");

        uint256 poolId = nextPoolId++;
        Pool storage pool = pools[poolId];
        
        pool.poolId = poolId;
        pool.creator = msg.sender;
        pool.name = name;
        pool.description = description;
        pool.targetAmount = targetAmount;
        pool.currentAmount = 0;
        pool.maxMembers = maxMembers;
        pool.currentMembers = 0;
        pool.lockDuration = lockDuration;
        pool.startTime = block.timestamp;
        pool.endTime = block.timestamp + lockDuration;
        pool.isCompleted = false;
        pool.isActive = true;
        pool.yieldEarned = 0;
        pool.apy = DEFAULT_APY;

        totalPoolsCreated++;
        userCreatedPools[msg.sender].push(poolId);

        emit PoolCreated(poolId, msg.sender, name, targetAmount, maxMembers, lockDuration);
    }

    /**
     * @dev Join a pool and make initial contribution
     * @param poolId Pool ID to join
     * @param amount Initial contribution amount (in wei)
     * @param useCUSD Whether to use cUSD instead of CELO
     * @param referrer Address of the person who referred this user (optional)
     */
    function joinPool(
        uint256 poolId,
        uint256 amount,
        bool useCUSD,
        address referrer
    ) external poolExists(poolId) poolActive(poolId) poolNotCompleted(poolId) nonReentrant {
        Pool storage pool = pools[poolId];
        
        require(!pool.isMember[msg.sender], "GroupPool: Already a member");
        require(pool.currentMembers < pool.maxMembers, "GroupPool: Pool is full");
        require(amount > 0, "GroupPool: Amount must be greater than 0");
        require(pool.currentAmount + amount <= pool.targetAmount, "GroupPool: Exceeds target amount");

        IERC20 token = useCUSD ? cusdToken : celoToken;
        
        // Transfer tokens from user to contract
        require(token.transferFrom(msg.sender, address(this), amount), "GroupPool: Transfer failed");
        
        // Add member to pool
        pool.members.push(msg.sender);
        pool.isMember[msg.sender] = true;
        pool.memberContributions[msg.sender] = amount;
        pool.currentMembers++;
        pool.currentAmount += amount;
        totalVolumeLocked += amount;

        userPools[msg.sender].push(poolId);

        emit MemberJoined(poolId, msg.sender, amount);

        // Pay referral bonus if referrer is valid
        if (referrer != address(0) && pool.isMember[referrer] && referrer != msg.sender) {
            uint256 bonusAmount = (amount * REFERRAL_BONUS_PERCENTAGE) / 10000;
            if (bonusAmount > 0) {
                require(token.transfer(referrer, bonusAmount), "GroupPool: Referral bonus failed");
                emit ReferralBonusPaid(poolId, referrer, bonusAmount);
            }
        }

        // Check if pool is completed
        if (pool.currentAmount >= pool.targetAmount) {
            _completePool(poolId);
        }
    }

    /**
     * @dev Make additional contribution to pool
     * @param poolId Pool ID
     * @param amount Amount to contribute (in wei)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function contributeToPool(
        uint256 poolId,
        uint256 amount,
        bool useCUSD
    ) external poolExists(poolId) poolActive(poolId) poolNotCompleted(poolId) nonReentrant {
        Pool storage pool = pools[poolId];
        
        require(pool.isMember[msg.sender], "GroupPool: Not a member");
        require(amount > 0, "GroupPool: Amount must be greater than 0");
        require(pool.currentAmount + amount <= pool.targetAmount, "GroupPool: Exceeds target amount");

        IERC20 token = useCUSD ? cusdToken : celoToken;
        
        // Transfer tokens from user to contract
        require(token.transferFrom(msg.sender, address(this), amount), "GroupPool: Transfer failed");
        
        pool.memberContributions[msg.sender] += amount;
        pool.currentAmount += amount;
        totalVolumeLocked += amount;

        emit ContributionMade(poolId, msg.sender, amount);

        // Check if pool is completed
        if (pool.currentAmount >= pool.targetAmount) {
            _completePool(poolId);
        }
    }

    /**
     * @dev Complete the pool and calculate yield
     * @param poolId Pool ID
     */
    function _completePool(uint256 poolId) internal {
        Pool storage pool = pools[poolId];
        pool.isCompleted = true;
        
        // Calculate yield based on APY and time locked
        uint256 timeLocked = block.timestamp - pool.startTime;
        uint256 yieldRate = (pool.apy * timeLocked) / (365 days * 10000); // Convert basis points to rate
        pool.yieldEarned = (pool.currentAmount * yieldRate) / 1e18;

        emit PoolCompleted(poolId, pool.currentAmount, pool.yieldEarned);
    }

    /**
     * @dev Withdraw funds after lock period ends
     * @param poolId Pool ID
     * @param useCUSD Whether to withdraw cUSD instead of CELO
     */
    function withdrawFromPool(uint256 poolId, bool useCUSD) 
        external 
        poolExists(poolId) 
        lockPeriodEnded(poolId) 
        nonReentrant 
    {
        Pool storage pool = pools[poolId];
        require(pool.isMember[msg.sender], "GroupPool: Not a member");
        require(!pool.hasWithdrawn[msg.sender], "GroupPool: Already withdrawn");
        require(pool.isCompleted, "GroupPool: Pool not completed");

        uint256 userContribution = pool.memberContributions[msg.sender];
        uint256 userShare = (userContribution * 1e18) / pool.currentAmount;
        uint256 userYield = (pool.yieldEarned * userShare) / 1e18;
        
        // Calculate fees
        uint256 yieldFee = (userYield * YIELD_FEE_PERCENTAGE) / 10000;
        uint256 creatorBonus = 0;
        
        // Creator gets bonus
        if (msg.sender == pool.creator) {
            creatorBonus = (userYield * CREATOR_BONUS_PERCENTAGE) / 10000;
        }
        
        uint256 totalWithdraw = userContribution + userYield - yieldFee + creatorBonus;

        IERC20 token = useCUSD ? cusdToken : celoToken;
        
        // Transfer tokens to user
        require(token.transfer(msg.sender, totalWithdraw), "GroupPool: Withdrawal failed");
        
        // Transfer fee to treasury
        if (yieldFee > 0) {
            require(token.transfer(treasury, yieldFee), "GroupPool: Fee transfer failed");
        }

        pool.hasWithdrawn[msg.sender] = true;
        totalVolumeLocked -= userContribution;

        emit FundsDistributed(poolId, msg.sender, totalWithdraw);
    }

    /**
     * @dev Get pool details
     * @param poolId Pool ID
     * @return poolId_ Pool ID
     * @return creator Creator address
     * @return name Pool name
     * @return description Pool description
     * @return targetAmount Target amount
     * @return currentAmount Current amount
     * @return maxMembers Maximum members
     * @return currentMembers Current members
     * @return lockDuration Lock duration
     * @return startTime Start time
     * @return endTime End time
     * @return isCompleted Is completed
     * @return isActive Is active
     * @return yieldEarned Yield earned
     * @return apy APY
     */
    function getPool(uint256 poolId) external view poolExists(poolId) returns (
        uint256 poolId_,
        address creator,
        string memory name,
        string memory description,
        uint256 targetAmount,
        uint256 currentAmount,
        uint256 maxMembers,
        uint256 currentMembers,
        uint256 lockDuration,
        uint256 startTime,
        uint256 endTime,
        bool isCompleted,
        bool isActive,
        uint256 yieldEarned,
        uint256 apy
    ) {
        Pool storage pool = pools[poolId];
        return (
            pool.poolId,
            pool.creator,
            pool.name,
            pool.description,
            pool.targetAmount,
            pool.currentAmount,
            pool.maxMembers,
            pool.currentMembers,
            pool.lockDuration,
            pool.startTime,
            pool.endTime,
            pool.isCompleted,
            pool.isActive,
            pool.yieldEarned,
            pool.apy
        );
    }

    /**
     * @dev Get member information for a pool
     * @param poolId Pool ID
     * @param member Member address
     * @return contribution Member contribution
     * @return share Member share
     * @return hasWithdrawn Has withdrawn
     */
    function getMemberInfo(uint256 poolId, address member) external view poolExists(poolId) returns (
        uint256 contribution,
        uint256 share,
        bool hasWithdrawn
    ) {
        Pool storage pool = pools[poolId];
        contribution = pool.memberContributions[member];
        share = pool.currentAmount > 0 ? (contribution * 1e18) / pool.currentAmount : 0;
        hasWithdrawn = pool.hasWithdrawn[member];
    }

    /**
     * @dev Get all members of a pool
     * @param poolId Pool ID
     * @return Array of member addresses
     */
    function getPoolMembers(uint256 poolId) external view poolExists(poolId) returns (address[] memory) {
        return pools[poolId].members;
    }

    /**
     * @dev Get user's pools
     * @param user User address
     * @return Array of pool IDs
     */
    function getUserPools(address user) external view returns (uint256[] memory) {
        return userPools[user];
    }

    /**
     * @dev Get user's created pools
     * @param user User address
     * @return Array of pool IDs
     */
    function getUserCreatedPools(address user) external view returns (uint256[] memory) {
        return userCreatedPools[user];
    }

    /**
     * @dev Get contract statistics
     * @return totalPools Total pools created
     * @return totalVolume Total volume locked
     * @return contractBalance CELO balance
     * @return cusdBalance cUSD balance
     */
    function getStats() external view returns (
        uint256 totalPools,
        uint256 totalVolume,
        uint256 contractBalance,
        uint256 cusdBalance
    ) {
        totalPools = totalPoolsCreated;
        totalVolume = totalVolumeLocked;
        contractBalance = celoToken.balanceOf(address(this));
        cusdBalance = cusdToken.balanceOf(address(this));
    }

    /**
     * @dev Update APY (only owner)
     * @param newAPY New APY in basis points
     */
    function updateAPY(uint256 newAPY) external onlyOwner {
        require(newAPY <= 2000, "GroupPool: APY too high"); // Max 20%
        DEFAULT_APY = newAPY;
    }

    /**
     * @dev Update treasury address (only owner)
     * @param newTreasury New treasury address
     */
    function updateTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "GroupPool: Invalid treasury address");
        treasury = newTreasury;
    }
}

contracts/LoopFi.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./SavingsGoal.sol";
import "./GroupPool.sol";
import "./SavingsBadgeNFT.sol";

/**
 * @title LoopFi
 * @dev Main contract integrating all LoopFi components
 * @author LoopFi Team
 */
contract LoopFi is ReentrancyGuard, Ownable {
    // Events
    event UserRegistered(address indexed user, string username);
    event YieldStrategyUpdated(address indexed strategy, uint256 apy);
    event BadgeAwarded(address indexed user, SavingsBadgeNFT.BadgeType badgeType, uint256 milestone);

    // Structs
    struct UserProfile {
        string username;
        uint256 totalSaved;
        uint256 totalYieldEarned;
        uint256 goalsCompleted;
        uint256 poolsJoined;
        uint256 poolsCreated;
        uint256 joinDate;
        bool isRegistered;
    }

    struct YieldStrategy {
        address strategyAddress;
        string name;
        uint256 apy; // Annual Percentage Yield in basis points
        bool isActive;
        uint256 totalDeposited;
    }

    // State variables
    mapping(address => UserProfile) public userProfiles;
    mapping(address => bool) public registeredUsers;
    
    SavingsGoal public savingsGoalContract;
    GroupPool public groupPoolContract;
    SavingsBadgeNFT public badgeNFTContract;
    
    IERC20 public celoToken;
    IERC20 public cusdToken;
    
    YieldStrategy[] public yieldStrategies;
    mapping(address => uint256) public strategyIndex;
    
    address public treasury;
    uint256 public totalUsers;
    uint256 public totalVolumeLocked;
    uint256 public totalYieldDistributed;
    
    // Constants
    uint256 public constant REGISTRATION_FEE = 0.001 ether; // 0.001 CELO registration fee
    uint256 public constant MAX_YIELD_STRATEGIES = 10;

    // Modifiers
    modifier onlyRegistered() {
        require(registeredUsers[msg.sender], "LoopFi: User not registered");
        _;
    }

    modifier validStrategy(address strategy) {
        require(strategyIndex[strategy] > 0, "LoopFi: Invalid yield strategy");
        _;
    }

    constructor(
        address _celoToken,
        address _cusdToken,
        address _treasury
    ) Ownable(msg.sender) {
        celoToken = IERC20(_celoToken);
        cusdToken = IERC20(_cusdToken);
        treasury = _treasury;
        
        // Deploy child contracts
        savingsGoalContract = new SavingsGoal(_celoToken, _cusdToken, _treasury);
        groupPoolContract = new GroupPool(_celoToken, _cusdToken, _treasury);
        badgeNFTContract = new SavingsBadgeNFT();
        
        // Transfer ownership of child contracts
        savingsGoalContract.transferOwnership(address(this));
        groupPoolContract.transferOwnership(address(this));
        badgeNFTContract.transferOwnership(address(this));
    }

    /**
     * @dev Register a new user
     * @param username Username for the user
     */
    function registerUser(string memory username) external payable nonReentrant {
        require(!registeredUsers[msg.sender], "LoopFi: User already registered");
        require(bytes(username).length > 0, "LoopFi: Username required");
        require(msg.value >= REGISTRATION_FEE, "LoopFi: Insufficient registration fee");

        UserProfile storage profile = userProfiles[msg.sender];
        profile.username = username;
        profile.totalSaved = 0;
        profile.totalYieldEarned = 0;
        profile.goalsCompleted = 0;
        profile.poolsJoined = 0;
        profile.poolsCreated = 0;
        profile.joinDate = block.timestamp;
        profile.isRegistered = true;

        registeredUsers[msg.sender] = true;
        totalUsers++;

        // Transfer registration fee to treasury
        if (msg.value > 0) {
            payable(treasury).transfer(msg.value);
        }

        emit UserRegistered(msg.sender, username);
    }

    /**
     * @dev Create a personal savings goal
     * @param targetAmount Target amount to save (in wei)
     * @param lockDuration Duration to lock funds (in seconds)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function createSavingsGoal(
        uint256 targetAmount,
        uint256 lockDuration,
        bool useCUSD
    ) external onlyRegistered {
        savingsGoalContract.createGoal(targetAmount, lockDuration, useCUSD);
    }

    /**
     * @dev Deposit to personal savings goal
     * @param amount Amount to deposit (in wei)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function depositToGoal(uint256 amount, bool useCUSD) external onlyRegistered {
        savingsGoalContract.deposit(amount, useCUSD);
        
        // Update user profile
        UserProfile storage profile = userProfiles[msg.sender];
        profile.totalSaved += amount;
    }

    /**
     * @dev Withdraw from personal savings goal
     * @param useCUSD Whether to withdraw cUSD instead of CELO
     */
    function withdrawFromGoal(bool useCUSD) external onlyRegistered {
        savingsGoalContract.withdraw(useCUSD);
        
        // Update user profile
        UserProfile storage profile = userProfiles[msg.sender];
        profile.goalsCompleted++;
    }

    /**
     * @dev Create a group pool
     * @param name Pool name
     * @param description Pool description
     * @param targetAmount Target amount to save (in wei)
     * @param maxMembers Maximum number of members
     * @param lockDuration Duration to lock funds (in seconds)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function createGroupPool(
        string memory name,
        string memory description,
        uint256 targetAmount,
        uint256 maxMembers,
        uint256 lockDuration,
        bool useCUSD
    ) external onlyRegistered {
        groupPoolContract.createPool(name, description, targetAmount, maxMembers, lockDuration, useCUSD);
        
        // Update user profile
        UserProfile storage profile = userProfiles[msg.sender];
        profile.poolsCreated++;
    }

    /**
     * @dev Join a group pool
     * @param poolId Pool ID to join
     * @param amount Initial contribution amount (in wei)
     * @param useCUSD Whether to use cUSD instead of CELO
     * @param referrer Address of the person who referred this user (optional)
     */
    function joinGroupPool(
        uint256 poolId,
        uint256 amount,
        bool useCUSD,
        address referrer
    ) external onlyRegistered {
        groupPoolContract.joinPool(poolId, amount, useCUSD, referrer);
        
        // Update user profile
        UserProfile storage profile = userProfiles[msg.sender];
        profile.totalSaved += amount;
        profile.poolsJoined++;
    }

    /**
     * @dev Contribute to group pool
     * @param poolId Pool ID
     * @param amount Amount to contribute (in wei)
     * @param useCUSD Whether to use cUSD instead of CELO
     */
    function contributeToGroupPool(
        uint256 poolId,
        uint256 amount,
        bool useCUSD
    ) external onlyRegistered {
        groupPoolContract.contributeToPool(poolId, amount, useCUSD);
        
        // Update user profile
        UserProfile storage profile = userProfiles[msg.sender];
        profile.totalSaved += amount;
    }

    /**
     * @dev Withdraw from group pool
     * @param poolId Pool ID
     * @param useCUSD Whether to withdraw cUSD instead of CELO
     */
    function withdrawFromGroupPool(uint256 poolId, bool useCUSD) external onlyRegistered {
        groupPoolContract.withdrawFromPool(poolId, useCUSD);
    }

    /**
     * @dev Add a yield strategy
     * @param strategyAddress Address of the yield strategy contract
     * @param name Name of the strategy
     * @param apy Annual Percentage Yield in basis points
     */
    function addYieldStrategy(
        address strategyAddress,
        string memory name,
        uint256 apy
    ) external onlyOwner {
        require(yieldStrategies.length < MAX_YIELD_STRATEGIES, "LoopFi: Max strategies reached");
        require(strategyIndex[strategyAddress] == 0, "LoopFi: Strategy already exists");
        require(apy <= 2000, "LoopFi: APY too high"); // Max 20%

        YieldStrategy memory strategy = YieldStrategy({
            strategyAddress: strategyAddress,
            name: name,
            apy: apy,
            isActive: true,
            totalDeposited: 0
        });

        yieldStrategies.push(strategy);
        strategyIndex[strategyAddress] = yieldStrategies.length;

        emit YieldStrategyUpdated(strategyAddress, apy);
    }

    /**
     * @dev Update yield strategy APY
     * @param strategyAddress Address of the strategy
     * @param newAPY New APY in basis points
     */
    function updateYieldStrategyAPY(address strategyAddress, uint256 newAPY) external onlyOwner validStrategy(strategyAddress) {
        require(newAPY <= 2000, "LoopFi: APY too high"); // Max 20%
        
        uint256 index = strategyIndex[strategyAddress] - 1;
        yieldStrategies[index].apy = newAPY;

        emit YieldStrategyUpdated(strategyAddress, newAPY);
    }

    /**
     * @dev Toggle strategy active status
     * @param strategyAddress Address of the strategy
     */
    function toggleStrategy(address strategyAddress) external onlyOwner validStrategy(strategyAddress) {
        uint256 index = strategyIndex[strategyAddress] - 1;
        yieldStrategies[index].isActive = !yieldStrategies[index].isActive;
    }

    /**
     * @dev Award badge to user
     * @param user User address
     * @param badgeType Type of badge
     * @param milestone Milestone achieved
     * @param metadataURI Metadata URI for the badge
     */
    function awardBadge(
        address user,
        SavingsBadgeNFT.BadgeType badgeType,
        uint256 milestone,
        string memory metadataURI
    ) external onlyOwner {
        badgeNFTContract.mintBadge(user, badgeType, milestone, metadataURI);
        emit BadgeAwarded(user, badgeType, milestone);
    }

    /**
     * @dev Get user profile
     * @param user User address
     * @return User profile
     */
    function getUserProfile(address user) external view returns (UserProfile memory) {
        return userProfiles[user];
    }

    /**
     * @dev Get user's badges
     * @param user User address
     * @return Array of badge types owned
     */
    function getUserBadges(address user) external view returns (SavingsBadgeNFT.BadgeType[] memory) {
        return badgeNFTContract.getUserBadges(user);
    }

    /**
     * @dev Get all yield strategies
     * @return Array of yield strategies
     */
    function getYieldStrategies() external view returns (YieldStrategy[] memory) {
        return yieldStrategies;
    }

    /**
     * @dev Get active yield strategies
     * @return Array of active yield strategies
     */
    function getActiveYieldStrategies() external view returns (YieldStrategy[] memory) {
        YieldStrategy[] memory activeStrategies = new YieldStrategy[](yieldStrategies.length);
        uint256 count = 0;
        
        for (uint256 i = 0; i < yieldStrategies.length; i++) {
            if (yieldStrategies[i].isActive) {
                activeStrategies[count] = yieldStrategies[i];
                count++;
            }
        }
        
        // Resize array
        YieldStrategy[] memory result = new YieldStrategy[](count);
        for (uint256 i = 0; i < count; i++) {
            result[i] = activeStrategies[i];
        }
        
        return result;
    }

    /**
     * @dev Get contract statistics
     * @return totalUsers_ Total registered users
     * @return totalVolume Total volume locked
     * @return totalYield Total yield distributed
     * @return contractBalance CELO balance
     * @return cusdBalance cUSD balance
     */
    function getStats() external view returns (
        uint256 totalUsers_,
        uint256 totalVolume,
        uint256 totalYield,
        uint256 contractBalance,
        uint256 cusdBalance
    ) {
        totalUsers_ = totalUsers;
        totalVolume = totalVolumeLocked;
        totalYield = totalYieldDistributed;
        contractBalance = address(this).balance;
        cusdBalance = cusdToken.balanceOf(address(this));
    }

    /**
     * @dev Update treasury address (only owner)
     * @param newTreasury New treasury address
     */
    function updateTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "LoopFi: Invalid treasury address");
        treasury = newTreasury;
        
        // Update child contracts
        savingsGoalContract.updateTreasury(newTreasury);
        groupPoolContract.updateTreasury(newTreasury);
    }

    /**
     * @dev Emergency withdraw (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(owner()).transfer(balance);
        }
    }

    /**
     * @dev Receive function to accept CELO
     */
    receive() external payable {}
}

